		if (supplier.getEmail().length() > 0 && !supplier.getEmail().matches(emailVerification))
			errors.rejectValue("email", "", "Địa chỉ email không hợp lệ");
		
		if(supplier.getEmail().length() > 100)
			errors.rejectValue("email", "", "Địa chỉ email không được lớn hơn 100 ký tự");
		
		if(supplier.getFax().length() > 100)
			errors.rejectValue("fax", "", "Số fax không được lớn hơn 100 ký tự");
		
		if (supplier.getPhone().length() > 0 && !supplier.getPhone().matches("^\\d{10}$"))
			errors.rejectValue("phone", "", "SĐT không hợp lệ");
		
		if(supplier.getPhone().length() > 25)
			errors.rejectValue("phone", "", "SĐT không được lớn hơn 25 ký tự");
